import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import Posted from '../components/Posted';
import more from '../images/more.png';

const DetailView = ({ post }) => {
  const [count, setCount] = useState(post.likes);
  const [comment, setComment] = useState('');
  const [comments, setComments] = useState([]);

  const updateCount = () => {
    setCount(count + 1);
  };

  const handleCommentSubmit = e => {
    e.preventDefault();
    setComments([...comments, comment]);
    setComment('');
  };

  return (
    <div className='DetailView'>
      <Posted created_at={post.created_at} />
      <Link to={'/'+ post.id + '/edit'}><img className="moreButton" alt="edit button" src={more} /></Link>

      <h2 className="title">{post.title}</h2> 

      <p className="content">{post.content}</p>
      <img className="image_url" src={post.image_url} alt="post" />
      <br></br>

      <button className="betButton" onClick={updateCount} >üëç Upvote: {count}</button>

      <form onSubmit={handleCommentSubmit}>
        <input type="text" value={comment} onChange={e => setComment(e.target.value)} placeholder="Add a comment" />
        <button type="submit">Submit</button>
      </form>

      {comments.map((comment, index) => (
        <p key={index}>{comment}</p>
      ))}
    </div>
  ); 
};

export default DetailView;
